  computed: {
   
  //  ...mapStores(useUserStore),
   ...mapWritableState(useUserStore, ['isAuthenticated', 'user']),


  },


  methods: {
    handleSubmit() {
      // Handle login logic here
      console.log('Login attempt:', this.username)
      if (!this.username.trim() || !this.password.trim()) {
        alert('Please enter both username and password')
        return
      }

      this.$refs.svg.style.removeProperty('fill');
      this.resultMsg = ""

      socket.emit('login', { username: this.username, password: this.password }, (response) => {

        if (response.success) {

          window.localStorage.setItem('selavy-login-token', response.login_token)
          this.$refs.svg.style.fill = "lightgreen"
          window.setTimeout(() => {
            this.isAuthenticated = true
            this.user = this.username

          }, 1000)


        } else {
          
          this.resultMsg = response.error
          this.$refs.svg.style.fill = "lightcoral"
        }
      })


    }
  },
  mounted() {
     // Component lifecycle hook when component is mounted
     this.$refs.username.focus()



     

  }
}
</script>

